    this.error = '';
    this.currentWeek = 1;
    this.currentSeason = new Date().getFullYear();
    // Stats
    this.currentWeekPicks = 0;
    this.seasonCorrect = 0;
    this.seasonWrong = 0;
    this.currentRank = 0;
    this.totalPoints = 0;
    // Games and picks
    this.upcomingGames = [];
    this.completedGames = [];
    this.userPicks = [];
    this.topPlayers = [];
